"""Binary Images

print an image with only two characters
"""

from shutil import get_terminal_size # used to get the width

class Image:
    """Store the data, width, and height of the image"""
    def __init__(self, data, width, height):
        self.data = data
        self.width = width
        self.height = height # see the bottom to learn how to get image data


IMAGES = {
    'monalisa': Image(
        '',
        34,
        50
    ),
    'soccerball': Image(
        '',
        80,
        80
    ),
    'car': Image(
        '',
        48,
        36
    ),
    'coffee': Image(
        '',
        22,
        22
    )
}


def print_image(image):
    """Print an image"""
    width = image.width
    height = image.height

    image = image.data.replace('0', ' ').replace('1', chr(9606)) # try replacing this with 0x25a9
    for i in range(width * (height - 1)):
        print(image[i], end='')

        if i % width == 0:
            print() # newline after each row

    print()


print_image(IMAGES['monalisa'])
print('-' * get_terminal_size()[0])

print_image(IMAGES['soccerball'])
print('-' * get_terminal_size()[0])

print_image(IMAGES['car'])
print('-' * get_terminal_size()[0])

print_image(IMAGES['coffee'])

